import styled from 'styled-components';
import { motion } from 'framer-motion';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { useState } from 'react';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  const [isPrinting, setIsPrinting] = useState(false);

  const addMusicSymbols = (ctx, width, height) => {
    const symbols = ['â™ª', 'â™«', 'â™¬', 'ðŸŽµ', 'ðŸŽ¶'];
    const symbolCount = 15; // Number of symbols to add
    ctx.font = '24px Arial';
    ctx.fillStyle = 'rgba(147, 51, 234, 0.1)'; // Very subtle purple

    for (let i = 0; i < symbolCount; i++) {
      const symbol = symbols[Math.floor(Math.random() * symbols.length)];
      const x = Math.random() * width;
      const y = Math.random() * height;
      const rotation = (Math.random() - 0.5) * Math.PI / 2;
      
      ctx.save();
      ctx.translate(x, y);
      ctx.rotate(rotation);
      ctx.fillText(symbol, 0, 0);
      ctx.restore();
    }
  };

  const captureSlide = async (element) => {
    const canvas = await html2canvas(element, {
      scale: 2,
      useCORS: true,
      logging: false,
      allowTaint: true,
      backgroundColor: '#0A0A0F',
      width: 1920,
      height: 1080,
      onclone: (clonedDoc) => {
        const elements = clonedDoc.getElementsByTagName('*');
        for (let elem of elements) {
          // Remove all backgrounds and gradients
          elem.style.background = 'none';
          elem.style.backgroundColor = 'transparent';
          
          // Remove shadows and highlights
          elem.style.boxShadow = 'none';
          elem.style.textShadow = 'none';
          
          // Remove animations and transitions
          elem.style.transform = 'none';
          elem.style.transition = 'none';
          elem.style.animation = 'none';
          
          // Remove backdrop filters
          elem.style.backdropFilter = 'none';
          elem.style.WebkitBackdropFilter = 'none';
          
          // Style headings with green
          if (elem.tagName === 'H1' || elem.tagName === 'H2' || elem.tagName === 'H3') {
            elem.style.backgroundColor = 'transparent';
            elem.style.color = '#22c55e'; // Green color for headings
          }

          // Style key points or bullet points with green
          if (elem.tagName === 'LI' || elem.classList.contains('key-point')) {
            elem.style.color = '#FFFFFF';
            // Add green bullet points
            if (elem.tagName === 'LI') {
              elem.style.listStyleType = 'none';
              elem.style.position = 'relative';
              elem.style.paddingLeft = '20px';
              elem.style.marginBottom = '10px';
              elem.style.position = 'relative';
              elem.innerHTML = `<span style="color: #22c55e; position: absolute; left: 0;">â€¢</span> ${elem.innerHTML}`;
            }
          }

          // Add gradient border to cards
          if (elem.classList.contains('glass-card')) {
            elem.style.border = '1px solid';
            elem.style.borderImage = 'linear-gradient(45deg, rgba(147, 51, 234, 0.5), rgba(34, 197, 94, 0.5)) 1';
            elem.style.borderRadius = '8px';
            elem.style.padding = '20px';
          }
        }

        // Set slide background
        const slides = clonedDoc.getElementsByClassName('slide');
        for (let slide of slides) {
          slide.style.backgroundColor = '#0A0A0F';
        }

        // Remove specific card backgrounds
        const cards = clonedDoc.getElementsByClassName('glass-card');
        for (let card of cards) {
          card.style.background = 'transparent';
          card.style.backgroundColor = 'transparent';
          // Ensure border is visible
          card.style.border = '1px solid rgba(147, 51, 234, 0.5)';
        }
      }
    });

    // Add music symbols to the background
    const ctx = canvas.getContext('2d');
    addMusicSymbols(ctx, canvas.width, canvas.height);

    return canvas.toDataURL('image/jpeg', 1.0);
  };

  const downloadPDF = async () => {
    setIsPrinting(true);
    
    try {
      const pdf = new jsPDF({
        orientation: 'landscape',
        unit: 'mm',
        format: 'a4',
        compress: true
      });

      const slides = Array.from(document.querySelectorAll('.slide'));
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();

      for (let i = 0; i < slides.length; i++) {
        if (i > 0) {
          pdf.addPage();
        }

        // Fill page background
        pdf.setFillColor(10, 10, 15); // #0A0A0F
        pdf.rect(0, 0, pdfWidth, pdfHeight, 'F');
        
        const imgData = await captureSlide(slides[i]);
        
        const aspectRatio = 16/9;
        const imgWidth = pdfWidth - 10; // Slight margin for border visibility
        const imgHeight = imgWidth / aspectRatio;
        const yOffset = (pdfHeight - imgHeight) / 2;
        const xOffset = 5; // Center the content
        
        pdf.addImage(imgData, 'JPEG', xOffset, yOffset, imgWidth, imgHeight);
        
        // Add gradient-like border effect using multiple strokes
        pdf.setDrawColor(147, 51, 234); // Purple
        pdf.setLineWidth(0.5);
        pdf.rect(xOffset, yOffset, imgWidth, imgHeight, 'S');
        
        pdf.setDrawColor(34, 197, 94); // Green
        pdf.setLineWidth(0.25);
        pdf.rect(xOffset + 1, yOffset + 1, imgWidth - 2, imgHeight - 2, 'S');
      }

      pdf.save('Wizkid_Crypto_Pitch_Deck.pdf');
    } catch (error) {
      console.error('PDF generation error:', error);
    } finally {
      setIsPrinting(false);
    }
  };

  return (
    <>
      <ExportButton onClick={downloadPDF}>
        Export to PDF
      </ExportButton>
      <DeckContainer>
        {[
          <CoverSlide key="cover" />,
          <BigIdeaSlide key="idea" />,
          <MarketStrategiesSlide key="market" />,
          <RevenuePotentialSlide key="revenue" />,
          <PartnershipsSlide key="partnerships" />,
          <DeliverableSlide key="deliverable" />,
          <ViralStrategySlide key="viral" />
        ].map((slide, index) => (
          <SlideWrapper key={index} className="slide" $isPrinting={isPrinting}>
            <SlideContent>
              {slide}
            </SlideContent>
          </SlideWrapper>
        ))}
      </DeckContainer>
    </>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 100%;
  margin: 0;
  padding: 0;
  background: ${props => props.theme.colors.background};
`;

const SlideWrapper = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  overflow: hidden;
  position: relative;
  background: ${props => props.theme.colors.background};
  page-break-after: always;
  
  ${props => props.$isPrinting && `
    height: 1080px;
    margin-bottom: 100px;
  `}
`;

const SlideContent = styled.div`
  width: 1920px;
  height: 1080px;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: center;
  transform: scale(0.6);
  background: ${props => props.theme.colors.background};
  padding: 40px;
`;

const ExportButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
  background: ${props => props.theme.colors.gradient.primary};
  color: ${props => props.theme.colors.secondary};
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: ${props => props.theme.shadows.card};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.card}, 0 0 15px rgba(255, 215, 0, 0.3);
  }
`;

export default PitchDeck;
