import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useState, useEffect } from 'react';

// Define Nigerian color palette
const colors = {
  green: '#008751',  // Nigerian Green
  white: '#FFFFFF',  // Pure White
  lightGreen: '#00A86B', // Lighter shade for gradients
  darkGreen: '#004D2C', // Darker shade for depth
  darkBg: '#0A1F14',    // Dark background
  lightText: 'rgba(255, 255, 255, 0.9)',
  darkText: '#0A0A0F'
};

const ShareOptions = styled.div`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
`;

const ShareButton = styled.button`
  padding: 12px 24px;
  background: linear-gradient(45deg, #FFD700, #FF1493);
  border: none;
  border-radius: 30px;
  color: white;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-2px);
  }
`;

const SharePopup = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: 10px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  padding: 20px;
  border-radius: 15px;
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const ShareLink = styled.input`
  width: 300px;
  padding: 10px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 5px;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  margin-bottom: 10px;
`;

const PitchDeck = () => {
  const [showShare, setShowShare] = useState(false);
  
  const copyToClipboard = () => {
    const url = window.location.href;
    navigator.clipboard.writeText(url);
    alert('Link copied to clipboard!');
  };

  const shareViaEmail = () => {
    const url = window.location.href;
