import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useState } from 'react';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  return (
    <DeckContainer>
      <SlideWrapper className="slide">
        <SlideContent>
          <CoverSlide />
        </SlideContent>
      </SlideWrapper>
      <SlideWrapper className="slide">
        <SlideContent>
          <BigIdeaSlide />
        </SlideContent>
      </SlideWrapper>
      <SlideWrapper className="slide">
        <SlideContent>
          <MarketStrategiesSlide />
        </SlideContent>
      </SlideWrapper>
      <SlideWrapper className="slide">
        <SlideContent>
          <RevenuePotentialSlide />
        </SlideContent>
      </SlideWrapper>
      <SlideWrapper className="slide">
        <SlideContent>
          <PartnershipsSlide />
        </SlideContent>
      </SlideWrapper>
      <SlideWrapper className="slide">
        <SlideContent>
          <DeliverableSlide />
        </SlideContent>
      </SlideWrapper>
      <SlideWrapper className="slide">
        <SlideContent>
          <ViralStrategySlide />
        </SlideContent>
      </SlideWrapper>
    </DeckContainer>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 100%;
  margin: 0;
  padding: 0;
  background: ${props => props.theme.colors.background};
`;

const SlideWrapper = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  overflow: hidden;
  position: relative;
  background: ${props => props.theme.colors.background};
`;

const SlideContent = styled.div`
  width: 1920px;
  height: 1080px;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: center;
  transform: scale(0.6);
  background: ${props => props.theme.colors.background};
  padding: 40px;
`;

export default PitchDeck;
