import styled from 'styled-components';
import { motion } from 'framer-motion';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { useState } from 'react';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  const [isPrinting, setIsPrinting] = useState(false);

  const backgroundImages = [
    'https://example.com/wizkid-performing.jpg',
    'https://example.com/made-in-lagos-cover.jpg',
    'https://example.com/wizkid-stage.jpg',
    'https://example.com/afrobeats-festival.jpg',
    'https://example.com/wizkid-awards.jpg',
    'https://example.com/starboy-entertainment.jpg',
    'https://example.com/wizkid-global.jpg'
  ];

  const addMusicSymbols = (ctx, width, height) => {
    const symbols = ['â™ª', 'â™«', 'â™¬', 'ðŸŽµ', 'ðŸŽ¶'];
    const symbolCount = 50;
    
    // Large background symbols
    ctx.font = '48px Arial';
    ctx.fillStyle = 'rgba(253, 224, 71, 0.08)';
    
    for (let i = 0; i < symbolCount; i++) {
      const symbol = symbols[Math.floor(Math.random() * symbols.length)];
      const x = Math.random() * width;
      const y = Math.random() * height;
      const rotation = (Math.random() - 0.5) * Math.PI;
      const scale = 1.2 + Math.random() * 0.8;
      
      ctx.save();
      ctx.translate(x, y);
      ctx.rotate(rotation);
      ctx.scale(scale, scale);
      ctx.fillText(symbol, 0, 0);
      ctx.restore();
    }
    
    // Medium symbols
    ctx.font = '36px Arial';
    ctx.fillStyle = 'rgba(234, 179, 8, 0.06)';
    for (let i = 0; i < symbolCount * 1.5; i++) {
      const symbol = symbols[Math.floor(Math.random() * symbols.length)];
      const x = Math.random() * width;
      const y = Math.random() * height;
      const rotation = (Math.random() - 0.5) * Math.PI;
      const scale = 0.8 + Math.random() * 0.6;
      
      ctx.save();
      ctx.translate(x, y);
      ctx.rotate(rotation);
      ctx.scale(scale, scale);
      ctx.fillText(symbol, 0, 0);
      ctx.restore();
    }
    
    // Small symbols
    ctx.font = '24px Arial';
    ctx.fillStyle = 'rgba(253, 224, 71, 0.04)';
    for (let i = 0; i < symbolCount * 2; i++) {
      const symbol = symbols[Math.floor(Math.random() * symbols.length)];
      const x = Math.random() * width;
      const y = Math.random() * height;
      const rotation = (Math.random() - 0.5) * Math.PI;
      const scale = 0.5 + Math.random() * 0.4;
      
      ctx.save();
      ctx.translate(x, y);
      ctx.rotate(rotation);
      ctx.scale(scale, scale);
      ctx.fillText(symbol, 0, 0);
      ctx.restore();
    }
  };

  const addPageBackground = async (ctx, width, height, pageIndex) => {
    try {
      const img = new Image();
      img.crossOrigin = 'Anonymous';
      
      // Load the background image for this page
      await new Promise((resolve, reject) => {
        img.onload = resolve;
        img.onerror = reject;
        img.src = backgroundImages[pageIndex % backgroundImages.length];
      });

      // Draw image with overlay
      ctx.globalAlpha = 0.15; // Very subtle background
      ctx.drawImage(img, 0, 0, width, height);
      ctx.globalAlpha = 1.0;

      // Add dark overlay to ensure content readability
      ctx.fillStyle = 'rgba(10, 10, 15, 0.85)';
      ctx.fillRect(0, 0, width, height);

      // Add music symbols on top
      addMusicSymbols(ctx, width, height);
    } catch (error) {
      console.error('Error loading background image:', error);
      // Fallback to just music symbols if image fails to load
      addMusicSymbols(ctx, width, height);
    }
  };

  const createBackgroundWithSymbols = async (width, height, pageIndex) => {
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext('2d');
    
    // Fill base background
    ctx.fillStyle = '#0A0A0F';
    ctx.fillRect(0, 0, width, height);
    
    // Add background image and symbols
    await addPageBackground(ctx, width, height, pageIndex);
    
    return canvas.toDataURL('image/jpeg', 1.0);
  };

  const downloadPDF = async () => {
    setIsPrinting(true);
    
    try {
      const pdf = new jsPDF({
        orientation: 'landscape',
        unit: 'mm',
        format: 'a4',
        compress: true
      });

      const slides = Array.from(document.querySelectorAll('.slide'));
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();

      for (let i = 0; i < slides.length; i++) {
        if (i > 0) {
          pdf.addPage();
        }

        // Create unique background for each page
        const backgroundImage = await createBackgroundWithSymbols(
          pdf.internal.pageSize.getWidth() * 2.834,
          pdf.internal.pageSize.getHeight() * 2.834,
          i
        );
        
        // Add background with image and symbols
        pdf.addImage(backgroundImage, 'JPEG', 0, 0, pdfWidth, pdfHeight);
        
        // Capture and add slide content
        const slideCanvas = await html2canvas(slides[i], {
          scale: 2,
          useCORS: true,
          logging: false,
          allowTaint: true,
          backgroundColor: 'transparent',
          width: 1920,
          height: 1080
        });
        
        const imgData = slideCanvas.toDataURL('image/jpeg', 1.0);
        
        const aspectRatio = 16/9;
        const imgWidth = pdfWidth - 10;
        const imgHeight = imgWidth / aspectRatio;
        const yOffset = (pdfHeight - imgHeight) / 2;
        const xOffset = 5;
        
        // Add slide content
        pdf.addImage(imgData, 'JPEG', xOffset, yOffset, imgWidth, imgHeight);
        
        // Add borders
        pdf.setDrawColor(147, 51, 234);
        pdf.setLineWidth(0.5);
        pdf.rect(xOffset, yOffset, imgWidth, imgHeight, 'S');
        
        pdf.setDrawColor(34, 197, 94);
        pdf.setLineWidth(0.25);
        pdf.rect(xOffset + 1, yOffset + 1, imgWidth - 2, imgHeight - 2, 'S');
      }

      pdf.save('Wizkid_Crypto_Pitch_Deck.pdf');
    } catch (error) {
      console.error('PDF generation error:', error);
    } finally {
      setIsPrinting(false);
    }
  };

  return (
    <>
      <ExportButton onClick={downloadPDF}>
        Export to PDF
      </ExportButton>
      <DeckContainer>
        {[
          <CoverSlide key="cover" />,
          <BigIdeaSlide key="idea" />,
          <MarketStrategiesSlide key="market" />,
          <RevenuePotentialSlide key="revenue" />,
          <PartnershipsSlide key="partnerships" />,
          <DeliverableSlide key="deliverable" />,
          <ViralStrategySlide key="viral" />
        ].map((slide, index) => (
          <SlideWrapper key={index} className="slide" $isPrinting={isPrinting}>
            <SlideContent>
              {slide}
            </SlideContent>
          </SlideWrapper>
        ))}
      </DeckContainer>
    </>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 100%;
  margin: 0;
  padding: 0;
  background: ${props => props.theme.colors.background};
`;

const SlideWrapper = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  overflow: hidden;
  position: relative;
  background: ${props => props.theme.colors.background};
  page-break-after: always;
  
  ${props => props.$isPrinting && `
    height: 1080px;
    margin-bottom: 100px;
  `}
`;

const SlideContent = styled.div`
  width: 1920px;
  height: 1080px;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: center;
  transform: scale(0.6);
  background: ${props => props.theme.colors.background};
  padding: 40px;
`;

const ExportButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
  background: ${props => props.theme.colors.gradient.primary};
  color: ${props => props.theme.colors.secondary};
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: ${props => props.theme.shadows.card};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.card}, 0 0 15px rgba(255, 215, 0, 0.3);
  }
`;

export default PitchDeck;
