import styled from 'styled-components';
import { motion } from 'framer-motion';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { useState } from 'react';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  const [isPrinting, setIsPrinting] = useState(false);

  const captureSlide = async (element) => {
    const canvas = await html2canvas(element, {
      scale: 2,
      useCORS: true,
      logging: false,
      allowTaint: true,
      backgroundColor: '#0A0A0F',
      width: 1920,
      height: 1080,
      onclone: (clonedDoc) => {
        // Remove all gradients and highlights
        const elements = clonedDoc.getElementsByTagName('*');
        for (let elem of elements) {
          elem.style.background = 'none';
          elem.style.backgroundColor = '#0A0A0F';
          elem.style.boxShadow = 'none';
          elem.style.textShadow = 'none';
          elem.style.transform = 'none';
          elem.style.transition = 'none';
          elem.style.animation = 'none';
          
          // Remove any backdrop filters
          elem.style.backdropFilter = 'none';
          elem.style.WebkitBackdropFilter = 'none';
          
          // Remove any border gradients
          elem.style.border = 'none';
          
          // Keep text colors
          if (elem.style.color) {
            elem.style.color = elem.style.color;
          }
        }

        // Set specific background for slide
        const slides = clonedDoc.getElementsByClassName('slide');
        for (let slide of slides) {
          slide.style.backgroundColor = '#0A0A0F';
        }
      }
    });
    return canvas.toDataURL('image/jpeg', 1.0);
  };

  const downloadPDF = async () => {
    setIsPrinting(true);
    await new Promise(resolve => setTimeout(resolve, 500));

    const pdf = new jsPDF({
      orientation: 'landscape',
      unit: 'mm',
      format: 'a4',
      compress: true,
      putOnlyUsedFonts: true
    });

    const slides = document.querySelectorAll('.slide');
    const pdfWidth = pdf.internal.pageSize.getWidth();
    const pdfHeight = pdf.internal.pageSize.getHeight();

    for (let i = 0; i < slides.length; i++) {
      try {
        if (i > 0) {
          pdf.addPage();
        }

        // Fill background
        pdf.setFillColor(10, 10, 15);
        pdf.rect(0, 0, pdfWidth, pdfHeight, 'F');
        
        const imgData = await captureSlide(slides[i]);
        
        const aspectRatio = 16/9;
        const imgWidth = pdfWidth;
        const imgHeight = pdfWidth / aspectRatio;
        const yOffset = (pdfHeight - imgHeight) / 2;
        
        pdf.addImage(imgData, 'JPEG', 0, yOffset, imgWidth, imgHeight, null, 'FAST');
        
      } catch (error) {
        console.error(`Error capturing slide ${i}:`, error);
      }
    }

    pdf.save('Wizkid_Crypto_Pitch_Deck.pdf');
    setIsPrinting(false);
  };

  return (
    <>
      <ExportButton onClick={downloadPDF}>
        Export to PDF
      </ExportButton>
      <DeckContainer $isPrinting={isPrinting}>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <CoverSlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <BigIdeaSlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <MarketStrategiesSlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <RevenuePotentialSlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <PartnershipsSlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <DeliverableSlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper className="slide" $isPrinting={isPrinting}>
          <SlideContent $isPrinting={isPrinting}>
            <ViralStrategySlide disableAnimations={isPrinting} />
          </SlideContent>
        </SlideWrapper>
      </DeckContainer>
    </>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 100%;
  margin: 0;
  padding: 0;
  background: ${props => props.theme.colors.background};
  
  ${props => props.$isPrinting && `
    * {
      background: none !important;
      background-color: #0A0A0F !important;
      box-shadow: none !important;
      text-shadow: none !important;
      backdrop-filter: none !important;
      -webkit-backdrop-filter: none !important;
      border: none !important;
    }
  `}
`;

const SlideWrapper = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  overflow: hidden;
  position: relative;
  background: ${props => props.theme.colors.background};
`;

const SlideContent = styled.div`
  width: 1920px;
  height: 1080px;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: center;
  transform: scale(0.6);
  background: ${props => props.theme.colors.background};
  padding: 40px;
  margin: 100px 0;

  ${props => props.$isPrinting && `
    transform: none;
  `}
`;

const ExportButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
  background: ${props => props.theme.colors.gradient.primary};
  color: ${props => props.theme.colors.secondary};
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: ${props => props.theme.shadows.card};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.card}, 0 0 15px rgba(255, 215, 0, 0.3);
  }
`;

export default PitchDeck;
