const theme = {
  colors: {
    primary: '#4B0082', // Deep purple for Wizkid's brand
    secondary: '#FFD700', // Gold accent
    background: '#1A1A1A', // Dark background
    text: '#FFFFFF',
    accent: '#E5C07B'
  },
  fonts: {
    heading: "'Clash Display', sans-serif",
    body: "'Inter', sans-serif"
  },
  breakpoints: {
    mobile: '320px',
    tablet: '768px',
    desktop: '1024px'
  }
};

export default theme;
