import styled from 'styled-components';
import { motion } from 'framer-motion';

const BigIdeaSlide = () => {
  return (
    <SlideContainer
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <Content>
        <Title>The Big Idea</Title>
        <FeatureList>
          <Feature>
            <Icon>üé∂</Icon>
            <FeatureText>First Afrobeats-inspired lifestyle crypto token</FeatureText>
          </Feature>
          <Feature>
            <Icon>üåç</Icon>
            <FeatureText>Bridges culture & decentralised finance</FeatureText>
          </Feature>
          <Feature>
            <Icon>üí∞</Icon>
            <FeatureText>Offers global accessibility to a new financial movement</FeatureText>
          </Feature>
        </FeatureList>
      </Content>
    </SlideContainer>
  );
};

const SlideContainer = styled(motion.div)`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: ${props => props.theme.colors.background};
  border-radius: 20px;
  margin-bottom: 20px;
  padding: 40px;
`;

const Content = styled.div`
  width: 100%;
  max-width: 800px;
`;

const Title = styled.h2`
  font-size: 3rem;
  margin-bottom: 3rem;
  color: ${props => props.theme.colors.secondary};
`;

const FeatureList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const Feature = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const Icon = styled.span`
  font-size: 2rem;
`;

const FeatureText = styled.p`
  font-size: 1.5rem;
  color: ${props => props.theme.colors.text};
`;

export default BigIdeaSlide;
