import styled from 'styled-components';
import { motion } from 'framer-motion';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { useState } from 'react';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  const [isPrinting, setIsPrinting] = useState(false);

  const addMusicSymbols = (ctx, width, height) => {
    try {
      const symbols = ['â™ª', 'â™«', 'â™¬', 'ðŸŽµ', 'ðŸŽ¶'];
      const symbolCount = 50;
      
      ctx.font = '48px Arial';
      ctx.fillStyle = 'rgba(253, 224, 71, 0.08)';
      
      for (let i = 0; i < symbolCount; i++) {
        const symbol = symbols[Math.floor(Math.random() * symbols.length)];
        const x = Math.random() * width;
        const y = Math.random() * height;
        const rotation = (Math.random() - 0.5) * Math.PI;
        
        ctx.save();
        ctx.translate(x, y);
        ctx.rotate(rotation);
        ctx.fillText(symbol, 0, 0);
        ctx.restore();
      }
    } catch (error) {
      console.error('Error adding music symbols:', error);
    }
  };

  const downloadPDF = async () => {
    setIsPrinting(true);
    
    try {
      const pdf = new jsPDF({
        orientation: 'landscape',
        unit: 'mm',
        format: 'a4',
        compress: true
      });

      const slides = Array.from(document.querySelectorAll('.slide'));
      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();

      for (let i = 0; i < slides.length; i++) {
        if (i > 0) {
          pdf.addPage();
        }

        // Fill background
        pdf.setFillColor(10, 10, 15);
        pdf.rect(0, 0, pdfWidth, pdfHeight, 'F');

        // Create canvas for background
        const bgCanvas = document.createElement('canvas');
        bgCanvas.width = pdfWidth * 2.834;
        bgCanvas.height = pdfHeight * 2.834;
        const bgCtx = bgCanvas.getContext('2d');
        
        // Fill background and add symbols
        bgCtx.fillStyle = '#0A0A0F';
        bgCtx.fillRect(0, 0, bgCanvas.width, bgCanvas.height);
        addMusicSymbols(bgCtx, bgCanvas.width, bgCanvas.height);
        
        // Add background to PDF
        pdf.addImage(bgCanvas.toDataURL('image/jpeg', 1.0), 'JPEG', 0, 0, pdfWidth, pdfHeight);
        
        // Capture slide content
        const slideCanvas = await html2canvas(slides[i], {
          scale: 2,
          useCORS: true,
          logging: false,
          allowTaint: true,
          backgroundColor: 'transparent',
          width: 1920,
          height: 1080,
          onclone: (clonedDoc) => {
            const elements = clonedDoc.getElementsByTagName('*');
            for (let elem of elements) {
              elem.style.background = 'none';
              elem.style.backgroundColor = 'transparent';
              
              if (elem.tagName === 'H1' || elem.tagName === 'H2' || elem.tagName === 'H3') {
                elem.style.color = '#FFFFFF';
                elem.style.backgroundColor = 'transparent';
              }
            }
          }
        });
        
        // Add slide content
        const aspectRatio = 16/9;
        const imgWidth = pdfWidth - 10;
        const imgHeight = imgWidth / aspectRatio;
        const yOffset = (pdfHeight - imgHeight) / 2;
        const xOffset = 5;
        
        pdf.addImage(slideCanvas.toDataURL('image/jpeg', 1.0), 'JPEG', xOffset, yOffset, imgWidth, imgHeight);
        
        // Add borders
        pdf.setDrawColor(147, 51, 234);
        pdf.setLineWidth(0.5);
        pdf.rect(xOffset, yOffset, imgWidth, imgHeight, 'S');
        
        pdf.setDrawColor(34, 197, 94);
        pdf.setLineWidth(0.25);
        pdf.rect(xOffset + 1, yOffset + 1, imgWidth - 2, imgHeight - 2, 'S');
      }

      pdf.save('Wizkid_Crypto_Pitch_Deck.pdf');
    } catch (error) {
      console.error('PDF generation error:', error);
    } finally {
      setIsPrinting(false);
    }
  };

  return (
    <>
      <DeckContainer>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <CoverSlide />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <BigIdeaSlide />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <MarketStrategiesSlide />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <RevenuePotentialSlide />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <PartnershipsSlide />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <DeliverableSlide />
          </SlideContent>
        </SlideWrapper>
        <SlideWrapper $isPrinting={isPrinting} className="slide">
          <SlideContent>
            <ViralStrategySlide />
          </SlideContent>
        </SlideWrapper>
      </DeckContainer>
      <ExportButton onClick={downloadPDF} disabled={isPrinting}>
        {isPrinting ? 'Generating PDF...' : 'Export PDF'}
      </ExportButton>
    </>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 100%;
  margin: 0;
  padding: 0;
  background: ${props => props.theme.colors.background};
`;

const SlideWrapper = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  overflow: hidden;
  position: relative;
  background: ${props => props.theme.colors.background};
  page-break-after: always;
  
  ${props => props.$isPrinting && `
    height: 1080px;
    margin-bottom: 100px;
  `}
`;

const SlideContent = styled.div`
  width: 1920px;
  height: 1080px;
  display: flex;
  align-items: center;
  justify-content: center;
  transform-origin: center;
  transform: scale(0.6);
  background: ${props => props.theme.colors.background};
  padding: 40px;
`;

const ExportButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
  background: ${props => props.theme.colors.gradient.primary};
  color: ${props => props.theme.colors.secondary};
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: ${props => props.theme.shadows.card};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.card}, 0 0 15px rgba(255, 215, 0, 0.3);
  }
`;

export default PitchDeck;
