import styled from 'styled-components';
import { motion } from 'framer-motion';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  const downloadPDF = async () => {
    const pdf = new jsPDF('p', 'mm', 'a4');
    const slides = document.querySelectorAll('.slide');
    
    for (let i = 0; i < slides.length; i++) {
      const slide = slides[i];
      
      const canvas = await html2canvas(slide, {
        scale: 2,
        useCORS: true,
        logging: false,
        allowTaint: true,
        backgroundColor: '#0A0A0F'
      });

      const imgData = canvas.toDataURL('image/jpeg', 1.0);
      
      if (i > 0) {
        pdf.addPage();
      }

      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = pdf.internal.pageSize.getHeight();
      
      pdf.addImage(imgData, 'JPEG', 0, 0, pdfWidth, pdfHeight);
    }
    
    pdf.save('Wizkid_Crypto_Pitch_Deck.pdf');
  };

  return (
    <>
      <ExportButton onClick={downloadPDF}>
        Export to PDF
      </ExportButton>
      <DeckContainer>
        <SlideWrapper className="slide">
          <CoverSlide />
        </SlideWrapper>
        <SlideWrapper className="slide">
          <BigIdeaSlide />
        </SlideWrapper>
        <SlideWrapper className="slide">
          <MarketStrategiesSlide />
        </SlideWrapper>
        <SlideWrapper className="slide">
          <RevenuePotentialSlide />
        </SlideWrapper>
        <SlideWrapper className="slide">
          <PartnershipsSlide />
        </SlideWrapper>
        <SlideWrapper className="slide">
          <DeliverableSlide />
        </SlideWrapper>
        <SlideWrapper className="slide">
          <ViralStrategySlide />
        </SlideWrapper>
      </DeckContainer>
    </>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

const SlideWrapper = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0;
  padding: 0;
  overflow: hidden;
  position: relative;
  background: ${props => props.theme.colors.background};
`;

const ExportButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
  background: ${props => props.theme.colors.gradient.primary};
  color: ${props => props.theme.colors.secondary};
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: ${props => props.theme.shadows.card};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.card}, 0 0 15px rgba(255, 215, 0, 0.3);
  }
`;

export default PitchDeck;
