import styled from 'styled-components';
import { motion } from 'framer-motion';
import html2pdf from 'html2pdf.js';
import CoverSlide from './slides/CoverSlide';
import BigIdeaSlide from './slides/BigIdeaSlide';
import MarketStrategiesSlide from './slides/MarketStrategiesSlide';
import RevenuePotentialSlide from './slides/RevenuePotentialSlide';
import PartnershipsSlide from './slides/PartnershipsSlide';
import DeliverableSlide from './slides/DeliverableSlide';
import ViralStrategySlide from './slides/ViralStrategySlide';

const PitchDeck = () => {
  const downloadPDF = () => {
    const element = document.getElementById('pitch-deck');
    const opt = {
      margin: 0,
      filename: 'Wizkid_Crypto_Pitch_Deck.pdf',
      image: { type: 'jpeg', quality: 0.98 },
      html2canvas: { 
        scale: 4,
        useCORS: true,
        letterRendering: true,
        windowWidth: 1200,
        windowHeight: 800
      },
      jsPDF: { 
        unit: 'mm', 
        format: 'a4', 
        orientation: 'portrait',
        compress: false
      },
      pagebreak: { mode: 'avoid-all', before: '.slide-break' }
    };

    html2pdf().set(opt).from(element).save();
  };

  return (
    <>
      <ExportButton onClick={downloadPDF}>
        Export to PDF
      </ExportButton>
      <DeckContainer id="pitch-deck">
        <SlideWrapper>
          <CoverSlide />
        </SlideWrapper>
        <SlideWrapper className="slide-break">
          <BigIdeaSlide />
        </SlideWrapper>
        <SlideWrapper className="slide-break">
          <MarketStrategiesSlide />
        </SlideWrapper>
        <SlideWrapper className="slide-break">
          <RevenuePotentialSlide />
        </SlideWrapper>
        <SlideWrapper className="slide-break">
          <PartnershipsSlide />
        </SlideWrapper>
        <SlideWrapper className="slide-break">
          <DeliverableSlide />
        </SlideWrapper>
        <SlideWrapper className="slide-break">
          <ViralStrategySlide />
        </SlideWrapper>
      </DeckContainer>
    </>
  );
};

const DeckContainer = styled.div`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
`;

const SlideWrapper = styled.div`
  height: 100vh;
  page-break-after: always;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const ExportButton = styled.button`
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 1000;
  background: ${props => props.theme.colors.gradient.primary};
  color: ${props => props.theme.colors.secondary};
  border: none;
  padding: 12px 24px;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: ${props => props.theme.shadows.card};

  &:hover {
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.card}, 0 0 15px rgba(255, 215, 0, 0.3);
  }
`;

export default PitchDeck;
